const burger=document.querySelector(".burger"),menu=document.querySelector(".nav"),overlay=document.querySelector(".header-overlay"),body=document.querySelector("body");function trigerClassses(s,e){s.classList.contains("show")?(body.classList.remove("stop-scroll"),overlay.classList.remove("show"),s.classList.remove("show"),e.classList.remove("show")):(body.classList.add("stop-scroll"),overlay.classList.add("show"),s.classList.add("show"),e.classList.add("show"))}burger.addEventListener("click",()=>{trigerClassses(burger,menu)}),body.addEventListener("click",s=>{s.target.classList.contains("header-overlay")&&trigerClassses(burger,menu)});